import React, { Component } from "react";
import { Form, Grid, Input, Button } from "semantic-ui-react";
import Layout from "./layout";

class change extends Component {
  state = { name: "", rollno: 0, branch: "" };

  render() {
    return (
      <Layout>
        {/* <Grid columns={3} divided>
          <Grid.Row> */}
        <Form onSubmit={this.changeName}>
          <Form.Field inline>
            <label>Name</label>
            <Input
              value={this.state.name}
              onChange={event => this.setState({ name: event.target.value })}
            />
            <Button style={{ marginLeft: "10px" }}>Submit</Button>
          </Form.Field>
        </Form>
        <Form onSubmit={this.changerollno}>
          <Form.Field inline>
            <label>rollno</label>
            <Input
              value={this.state.rollno}
              onChange={event => this.setState({ rollno: event.target.value })}
            />
            <Button style={{ marginLeft: "10px" }}>Submit</Button>
          </Form.Field>
        </Form>
        <Form onSubmit={this.changebranch}>
          <Form.Field inline>
            <label>.branch</label>
            <Input
              value={this.state.branch}
              onChange={event => this.setState({ branch: event.target.value })}
            />
            <Button style={{ marginLeft: "10px" }}>Submit</Button>
          </Form.Field>
        </Form>

        {/* </Grid.Row>
        </Grid> */}
      </Layout>
    );
  }
}

export default change;
