import React, { Component } from "react";
import { Form, Grid, Input, Button } from "semantic-ui-react";
import Layout from "./layout";
import web3 from "../ethereum/web3";
import ait from "../ethereum/Ait";

class change extends Component {
  state = { name: "", rollno: 0, branch: "", regno: 0, dob: "", bloodGp: " " };
  changeName = async () => {
    const accounts = await web3.eth.getAccounts();
    await ait.methods
      .changeName(this.state.name, this.state.regno)
      .send({ from: accounts[0] });
    // console.log(this.state.regno);
  };
  changeRollNo = async () => {
    const accounts = await web3.eth.getAccounts();
    await ait.methods
      .changeRollno(this.state.rollno, this.state.regno)
      .send({ from: accounts[0] });
  };
  changeBranch = async () => {
    const accounts = await web3.eth.getAccounts();
    await ait.methods
      .changeBranch(this.state.branch, this.state.regno)
      .send({ from: accounts[0] });
  };
  changeDob = async () => {
    const accounts = await web3.eth.getAccounts();
    await ait.methods
      .changeDob(this.state.dob, this.state.regno)
      .send({ from: accounts[0] });
  };
  changeBloodGp = async () => {
    const accounts = await web3.eth.getAccounts();
    await ait.methods
      .changeDob(this.state.dob, this.state.regno)
      .send({ from: accounts[0] });
  };

  render() {
    return (
      <Layout>
        <Grid>
          <Grid.Row>
            <Form>
              <Form.Field inline>
                <label>Reg No. </label>
                <Input
                  value={this.state.regno}
                  onChange={event =>
                    this.setState({ regno: event.target.value })
                  }
                />
              </Form.Field>
            </Form>
          </Grid.Row>
          <Grid.Row>
            <Form onSubmit={this.changeName}>
              <Form.Field inline>
                <label>Name</label>
                <Input
                  value={this.state.name}
                  onChange={event =>
                    this.setState({ name: event.target.value })
                  }
                />
                <Button style={{ marginLeft: "10px" }}>Submit</Button>
              </Form.Field>
            </Form>
          </Grid.Row>
          <Grid.Row>
            <Form onSubmit={this.changeRollNo}>
              <Form.Field inline>
                <label>Roll No</label>
                <Input
                  value={this.state.rollno}
                  onChange={event =>
                    this.setState({ rollno: event.target.value })
                  }
                />
                <Button style={{ marginLeft: "10px" }}>Submit</Button>
              </Form.Field>
            </Form>
          </Grid.Row>
          <Grid.Row>
            <Form onSubmit={this.changeBranch}>
              <Form.Field inline>
                <label>Branch</label>
                <Input
                  value={this.state.branch}
                  onChange={event =>
                    this.setState({ branch: event.target.value })
                  }
                />
                <Button style={{ marginLeft: "10px" }}>Submit</Button>
              </Form.Field>
            </Form>
          </Grid.Row>
          <Grid.Row>
            <Form onSubmit={this.changeDob}>
              <Form.Field inline>
                <label>Date of Birth</label>
                <Input
                  value={this.state.dob}
                  onChange={event => this.setState({ dob: event.target.value })}
                />
                <Button style={{ marginLeft: "10px" }}>Submit</Button>
              </Form.Field>
            </Form>
          </Grid.Row>
          <Grid.Row>
            <Form onSubmit={this.changeBloodGp}>
              <Form.Field inline>
                <label>Blood Group</label>
                <Input
                  value={this.state.bloodGp}
                  onChange={event =>
                    this.setState({ bloodGp: event.target.value })
                  }
                />
                <Button style={{ marginLeft: "10px" }}>Submit</Button>
              </Form.Field>
            </Form>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}

export default change;
