import React, { Component } from "react";
import { Form, Grid, Input, Button } from "semantic-ui-react";
import Layout from "./layout";
import web3 from "../ethereum/web3";
import ait from "../ethereum/Ait";

class change extends Component {
  state = {
    name: "",
    rollno: "",
    branch: "",
    regno: "",
    dob: "",
    bloodGp: " ",
    nloading: false,
    rloading: false,
    bloading: false,
    dloading: false,
    gloading: false,
    lloading: false,
    nerrmsg: false,
    rerrmsg: false,
    berrmsg: false,
    derrmsg: false,
    gerrmsg: false,
    lerrmsg: false
  };
  changeName = async () => {
    this.setState({ nloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods
        .changeName(this.state.name, this.state.regno)
        .send({ from: accounts[0] });
      // console.log(this.state.regno);
    } catch (error) {
      this.setState({ nerrmsg: error.message });
    }
    this.setState({ nloading: false });
  };

  changeRollNo = async () => {
    this.setState({ rloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods
        .changeRollno(this.state.rollno, this.state.regno)
        .send({ from: accounts[0] });
    } catch (error) {
      this.setState({ rerrmsg: error.message });
    }
    this.setState({ rloading: false });
  };

  changeBranch = async () => {
    this.setState({ bloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods
        .changeBranch(this.state.branch, this.state.regno)
        .send({ from: accounts[0] });
    } catch (error) {
      this.setState({ berrmsg: error.message });
    }
    this.setState({ bloading: false });
  };

  changeDob = async () => {
    this.setState({ dloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods
        .changeDob(this.state.dob, this.state.regno)
        .send({ from: accounts[0] });
    } catch (error) {
      this.setState({ derrmsg: error.message });
    }

    this.setState({ dloading: false });
  };

  changeBloodGp = async () => {
    this.setState({ gloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods
        .changeBloodGp(this.state.bloodGp, this.state.regno)
        .send({ from: accounts[0] });
    } catch (error) {
      this.setState({ gerrmsg: error.message });
    }

    this.setState({ gloading: false });
  };
  left = async () => {
    this.setState({ lloading: true });
    try {
      const accounts = await web3.eth.getAccounts();
      await ait.methods.left(this.state.regno).send({ from: accounts[0] });
      this.setState({ lloading: false });
    } catch (error) {}
  };

  render() {
    return (
      <Layout>
        <Grid>
          <Grid.Row>
            <Grid.Column>
              <Form>
                <Form.Field inline>
                  <label>Reg No. </label>
                  <Input
                    value={this.state.regno}
                    onChange={event =>
                      this.setState({ regno: event.target.value })
                    }
                  />
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.changeName}>
                <Form.Field inline>
                  <label>Name :</label>
                  <Input
                    value={this.state.name}
                    onChange={event =>
                      this.setState({ name: event.target.value })
                    }
                  />
                  <Button
                    positive
                    style={{ marginLeft: "10px" }}
                    loading={this.state.nloading}
                  >
                    Submit
                  </Button>
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.changeRollNo}>
                <Form.Field inline>
                  <label>Roll No :</label>
                  <Input
                    value={this.state.rollno}
                    onChange={event =>
                      this.setState({ rollno: event.target.value })
                    }
                  />
                  <Button
                    positive
                    style={{ marginLeft: "10px" }}
                    loading={this.state.rloading}
                  >
                    Submit
                  </Button>
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.changeBranch}>
                <Form.Field inline>
                  <label>Branch :</label>
                  <Input
                    value={this.state.branch}
                    onChange={event =>
                      this.setState({ branch: event.target.value })
                    }
                  />
                  <Button
                    positive
                    style={{ marginLeft: "10px" }}
                    loading={this.state.bloading}
                  >
                    Submit
                  </Button>
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.changeDob}>
                <Form.Field inline>
                  <label>Date of Birth :</label>
                  <Input
                    value={this.state.dob}
                    onChange={event =>
                      this.setState({ dob: event.target.value })
                    }
                  />
                  <Button
                    positive
                    style={{ marginLeft: "10px" }}
                    loading={this.state.dloading}
                  >
                    Submit
                  </Button>
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.changeBloodGp}>
                <Form.Field inline>
                  <label>Blood Group :</label>
                  <Input
                    value={this.state.bloodGp}
                    onChange={event =>
                      this.setState({ bloodGp: event.target.value })
                    }
                  />
                  <Button
                    positive
                    style={{ marginLeft: "10px" }}
                    loading={this.state.gloading}
                  >
                    Submit
                  </Button>
                </Form.Field>
              </Form>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form onSubmit={this.left}>
                <Button
                  positive
                  style={{ marginTop: "10px" }}
                  negative
                  loading={this.state.lloading}
                >
                  LEFT!!!!
                </Button>
              </Form>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}

export default change;
