import React, { Component } from "react";
import web3 from "../ethereum/web3";
import Layout from "./layout";
import ait from "../ethereum/Ait";
import { Form, Button, Input, Table } from "semantic-ui-react";
class Index extends Component {
  state = {
    regno: " ",
    name: " ",
    rollno: "",
    branch: " ",
    dob: " ",
    bloodGp: "",
    isStudent: " ",
    loading: false
  };
  onSubmit = async () => {
    event.preventDefault();
    // console.log("cheacking");
    const accounts = await web3.eth.getAccounts();
    // console.log(accounts[0]);

    const student = await ait.methods.student(this.state.regno).call();
    this.setState({
      regno: student.name,
      rollno: student.rollno,
      branch: student.branch,
      dob: student.dob,
      bloodGp: student.bloodGp
    });
    // console.log(student.name);
  };
  render() {
    const { student } = this.state;
    return (
      <Layout>
        <h1>it is index page</h1>
        <Form onSubmit={this.onSubmit}>
          <Form.Field>
            <label>regno</label>
            <Input
              value={this.state.regno}
              onChange={event => this.setState({ regno: event.target.value })}
            />
          </Form.Field>
          <Button primary>Submit</Button>
        </Form>
        <Table>
          <Table.Body>
            <Table.Row>
              <Table.Cell>Name</Table.Cell>
              <Table.Cell>Approved</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>Jamie</Table.Cell>
              <Table.Cell>Approved</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>Jill</Table.Cell>
              <Table.Cell>Denied</Table.Cell>
            </Table.Row>
          </Table.Body>
        </Table>
      </Layout>
    );
  }
}

export default Index;
